mapper.select:
SELECT t.`id`, t.`ip`, t.`port`, t.`name`, t.`version`, t.`os_id` as osId, t.`os_type` as osType, t.`os_version` as osVersion, t.`osbit`, t.`account_id` as accountId, t.`runner_id` as runnerId, t.`disconnect_reason` as disConnectReason, ar.`name` as runnerName, t.`runner_group_id` as runnerGroupId, t.`runner_ip` as runnerIp, t.`runner_port` as runnerPort, ti.ip as tagentIp, t.`user`, t.`pcpu`, t.`mem`, t.`status`, t.`fcd`, t.`lcd`, tos.`name` as osName, apg.`name` as runnerGroupName FROM ( SELECT DISTINCT t.`id` FROM tagent t LEFT JOIN `runner` ar ON t.`runner_id` = ar.`id` LEFT JOIN `runnergroup` apg ON t.`runner_group_id` = apg.`id` LEFT JOIN `tagent_ip` ti ON t.`id` = ti.`tagent_id`
LEFT JOIN `tagent_os` tos ON tos.`id` = t.`os_id`
AND t.`os_id` = #{osId}
AND tos.`name` = #{osName}
AND t.`version` = #{version}
AND t.`status` = #{status}
AND t.`runner_group_id` = #{runnerGroupId}
AND (t.`ip` LIKE CONCAT('%',#{keyword},'%') OR t.`name` LIKE CONCAT('%',#{keyword},'%') OR t.`os_version` LIKE CONCAT('%',#{keyword},'%') OR ti.`ip` LIKE CONCAT('%',#{keyword},'%'))
ORDER BY t.`id` DESC
LIMIT #{startNum}, #{pageSize} ) a LEFT JOIN `tagent` t ON t.id = a.`id` LEFT JOIN `runner` ar ON t.`runner_id` = ar.`id` LEFT JOIN `runnergroup` apg ON t.`runner_group_id` = apg.`id` LEFT JOIN `tagent_ip` ti ON t.`id` = ti.`tagent_id` LEFT JOIN `tagent_os` tos ON t.`os_id` = tos.`id` ORDER BY t.`id` DESC

select count(distinct t.`id`)
from tagent t
LEFT JOIN `tagent_ip` ti ON t.`id` = ti.`tagent_id`

    LEFT JOIN `tagent_os` tos ON tos.`id` = t.`os_id`



        AND t.`os_id` = #{osId}


        AND tos.`name` = #{osName}


        AND t.`version` = #{version}


        AND t.`status` = #{status}


        AND t.`runner_group_id` = #{runnerGroupId}


        AND (t.`ip` LIKE CONCAT('%',#{keyword},'%')
        OR t.`name` LIKE CONCAT('%',#{keyword},'%')
        OR t.`os_version` LIKE CONCAT('%',#{keyword},'%')
        OR ti.`ip` LIKE CONCAT('%',#{keyword},'%'))

select count(1)
from tagent_version
where `file_id` = #{fileId}

select `account_id`
from tagent
where id = #{id}

select tv.`id`, tv.`version`, tv.`os_type` as osType, tv.`osbit`, f.`id` as fileId, f.`name` as fileName, f.`size`, f.`user_uuid` as fcu, f.`upload_time` as fcd from tagent_version tv left join file f on tv.`file_id` = f.`id`
and tv.`os_type` = #{osType}
and tv.`version` = #{version}
and tv.`osbit` = #{osbit}
order by `id` desc
LIMIT #{startNum}, #{pageSize}

select a.`id`, a.`count`, a.`fcd`, a.`network`, a.`fcu` from tagent_upgrade_audit a left join user u on a.`fcu` = u.`uuid`
where u.`user_name` = #{fcuName}
order by a.`id` desc
LIMIT #{startNum}, #{pageSize}

select count(`id`)
from tagent_upgrade_detail
where `audit_id` = #{auditId}

    and `status` =#{status}


    and ip LIKE CONCAT('%', #{ip}, '%')

select `id`, `audit_id`, `ip`, `port`, `source_version` as sourceVersion, `target_version` as targetVersion, `result`, `status` from tagent_upgrade_detail where `audit_id` = #{auditId}
and `status` =#{status}
and `ip` =#{ip}
order by `id` desc
LIMIT #{startNum}, #{pageSize}

select `id`,
       `os_type`     as osType,
       `version`,
       `osbit`,
       `ignore_file` as ignoreFile,
       `file_id`     as fileId
from tagent_version
where `version` = #{version}
  and `os_type` = #{osType}
  and `osbit` = #{osbit}

SELECT ti.ip
FROM tagent_ip ti
         LEFT JOIN tagent t ON t.id = ti.tagent_id
WHERE t.ip = #{ip}
    AND t.port=#{port}

SELECT t.id
FROM tagent t
where t.ip = #{ip}
  and t.port = #{port}

SELECT t.id, t.ip, t.port, t.name AS name, t.version AS version, t.os_type AS osType, t.os_version AS osVersion, t.osbit AS osbit, t.account_id AS accountId, t.status, t.pcpu, t.mem, t.user AS user, t.runner_id AS runnerId, t.runner_ip AS runnerIp, t.runner_port AS runnerPort, t.runner_group_id AS runnerGroupId, t.fcd AS fcd, t.lcd AS lcd FROM tagent t where t.ip in
#{ip}
and t.port = #{port} and t.id != #{tagentId}

SELECT ti.ip
FROM tagent_ip ti
WHERE ti.tagent_id = #{value}

select crai.ip from `tagent_account` cra LEFT JOIN `tagent_account_ip` crai on crai.account_id = cra.id LEFT JOIN `cmdb_resourcecenter_account_protocol` crap on cra.protocol_id = crap.id where crai.ip in
#{ip}
and crap.`port` = #{port}

mapper.delete:
DELETE
FROM tagent_ip
WHERE
tagent_id = #{tagentId}
AND ip = #{ip}
